## Display Office 365 Toast Notification Remediation Script
## To be used in a compliance item

## References
# Options for audio: https://docs.microsoft.com/en-us/uwp/schemas/tiles/toastschema/element-audio#attributes-and-elements
# Toast content schema: https://docs.microsoft.com/en-us/windows/uwp/design/shell/tiles-and-notifications/toast-schema
# Datetime format for deadline: Ref: https://msdn.microsoft.com/en-us/library/system.datetime(v=vs.110).aspx

# Required parameters
$Title = "Microsoft 365 Self-Service"
$headertitle = "Microsoft 365"
#Link to Deployment... Copy link from Software Center on the App to here:
$SoftwarecenterShortcut= "softwarecenter:SoftwareID=ScopeId_7520050B-F9BC-452E-AD92-210A44FC8FDE/Application_645ae0df-7206-4573-8392-7f76a10449e1"

#Learn More Button:
$LearnMore = "https://www.recastsoftware.com/blog"
$AudioSource = "ms-winsoundevent:Notification.Default"
$SubtitleText = "Microsoft 365 Now Available"
$BodyText = "Microsoft 365 replaces your current version of Office.  Your Office applications will need to close"

$HeaderFormat = "ImageOnly" # Choose from "TitleOnly", "ImageOnly" or "ImageAndTitle"

# Optional parameters
# Base64 string for an image, see Images section below to create the string

#Office 365 Image
$Base64Image = ""
#Office Logo
$Base64Logo = ""
# Deployment deadline
#[datetime]$Deadline = "21 June 2018 15:00"

Add-Type -Path $env:windir\CCM\SCClient.data.dll
$Connector = [Microsoft.SoftwareCenter.Client.Data.ClientConnectionFactory]::CreateDataConnector()
$Package = $Connector.AllProgramApplications | where {$_.PackageId -eq $TSPackageID}
#$Connector.InstallApplication($Package,$false,$false);$Connector.Dispose()

$logfile = "$env:TEMP\o365_Baseline.log"

#region: CMTraceLog Function formats logging in CMTrace style
        function CMTraceLog {
         [CmdletBinding()]
    Param (
		    [Parameter(Mandatory=$false)]
		    $Message,
 
		    [Parameter(Mandatory=$false)]
		    $ErrorMessage,
 
		    [Parameter(Mandatory=$false)]
		    $Component = "365 Toast Remediation",
 
		    [Parameter(Mandatory=$false)]
		    [int]$Type,
		
		    [Parameter(Mandatory=$true)]
		    $LogFile
	    )
    <#
    Type: 1 = Normal, 2 = Warning (yellow), 3 = Error (red)
    #>
	    $Time = Get-Date -Format "HH:mm:ss.ffffff"
	    $Date = Get-Date -Format "MM-dd-yyyy"
 
	    if ($ErrorMessage -ne $null) {$Type = 3}
	    if ($Component -eq $null) {$Component = " "}
	    if ($Type -eq $null) {$Type = 1}
 
	    $LogMessage = "<![LOG[$Message $ErrorMessage" + "]LOG]!><time=`"$Time`" date=`"$Date`" component=`"$Component`" context=`"`" type=`"$Type`" thread=`"`" file=`"`">"
	    $LogMessage | Out-File -Append -Encoding UTF8 -FilePath $LogFile
    }

CMTraceLog -Message  "-------------------------------------------------" -Type 1 -LogFile $LogFile
CMTraceLog -Message  "Starting Office 365 Toast Notification Script" -Type 2 -LogFile $LogFile
CMTraceLog -Message  "-------------------------------------------------" -Type 1 -LogFile $LogFile

# Calculated parameters
If ($Deadline)
{
    $TimeSpan = $Deadline - [datetime]::Now
}

# Create an image file from base64 string and save to user temp location
If ($Base64Image)
{
    $ImageFile = "$env:TEMP\ToastImage.png"
    [byte[]]$Bytes = [convert]::FromBase64String($Base64Image)
    [System.IO.File]::WriteAllBytes($ImageFile,$Bytes)
}

# Create an image file from base64 string and save to user temp location
If ($Base64Logo)
{
    $ImageLogo = "$env:TEMP\ToastLogo.png"
    [byte[]]$Bytes = [convert]::FromBase64String($Base64Logo)
    [System.IO.File]::WriteAllBytes($ImageLogo,$Bytes)
}

# Load some required namespaces
$null = [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime]
$null = [Windows.Data.Xml.Dom.XmlDocument, Windows.Data.Xml.Dom.XmlDocument, ContentType = WindowsRuntime]

# Register the AppID in the registry for use with the Action Center, if required

# Path to the notification app doing the actual toast
$RegPath = "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Notifications\Settings"
$App = "Microsoft.SoftwareCenter.DesktopToasts"

# Creating registry entries if they don't exists
if (-NOT(Test-Path -Path "$RegPath\$App")) {
   New-Item -Path "$RegPath\$App" -Force
   New-ItemProperty -Path "$RegPath\$App" -Name "ShowInActionCenter" -Value 1 -PropertyType "DWORD" -Force
   New-ItemProperty -Path "$RegPath\$App" -Name "Enabled" -Value 1 -PropertyType "DWORD" -Force
}

# Define the toast notification in XML format
[xml]$ToastTemplate = @"
<toast scenario="reminder">
    <visual>
    <binding template="ToastGeneric">
        <image placement="appLogoOverride" src="$ImageLogo" />
        <text>$headertitle</text>
        <text placement="attribution"></text>        
        <group>
            <subgroup>
                <text hint-style="title" hint-wrap="true" >$Title</text>
           </subgroup>
        </group>
        <group>          
            <subgroup>     
                <text hint-style="subtitle" hint-wrap="true" >$SubtitleText</text>
            </subgroup>
        </group>
        <group>
            <subgroup>     
                <text hint-style="body" hint-wrap="true" >$BodyText</text>
            </subgroup>
        </group>
    </binding>
    </visual>
    <actions>
      <input id="snoozeTime" type="selection" title="Remind me in:" defaultInput="15">
          <selection id="15" content="15 minutes"/>
          <selection id="30" content="30 minutes"/>
          <selection id="60" content="1 hour"/>
          <selection id="240" content="4 hours"/>
       </input>
      <action content="Learn More" activationType="protocol" arguments="$LearnMore" /> 
      <action activationType="system" arguments="snooze" hint-inputId="snoozeTime" content="" />
      <action content="Get Started" activationType="protocol" arguments="$SoftwarecenterShortcut" />   
    </actions>
    <audio silent="true"/>
</toast>
"@


# Add a deadline if required
If ($Deadline)
{
[xml]$ToastTemplateDeadline = @"
<toast scenario="reminder">
    <visual>
    <binding template="ToastGeneric">
        <image placement="appLogoOverride" src="$ImageLogo" />
        <text>GARYTOWN</text>
        <text placement="attribution"></text>        
        <group>
            <subgroup>
                <text hint-style="title" hint-wrap="true" >$Title</text>
            </subgroup>
        </group>
        <group>          
            <subgroup>     
                <text hint-style="subtitle" hint-wrap="true" >$SubtitleText</text>
            </subgroup>
        </group>
        <group>
            <subgroup>     
                <text hint-style="body" hint-wrap="true" >$BodyText</text>
            </subgroup>
        </group>
        <group>
            <subgroup>
                <text hint-style="base" hint-align="left">Deadline</text>
                 <text hint-style="caption" hint-align="left">$(Get-Date -Date $Deadline -Format "G")</text>
            </subgroup>
            <subgroup>
                <text hint-style="base" hint-align="right">Time Remaining </text>
                <text hint-style="caption" hint-align="right">$($TimeSpan.Days) days $($TimeSpan.Hours) hours $($TimeSpan.Minutes) minutes  .</text>
            </subgroup>
        </group>

    </binding>
    </visual>
    <actions>
      <input id="snoozeTime" type="selection" title="Remind me in:" defaultInput="15">
          <selection id="15" content="15 minutes"/>
          <selection id="30" content="30 minutes"/>
          <selection id="60" content="1 hour"/>
          <selection id="240" content="4 hours"/>
       </input>
      <action content="Learn More" activationType="protocol" arguments="$LearnMore" /> 
      <action activationType="system" arguments="snooze" hint-inputId="snoozeTime" content="" />
      <action content="Get Started" activationType="protocol" arguments="$SoftwarecenterShortcut"/>   
    </actions>
    <audio silent="true"/>
</toast>
"@

$DeadlineGroups = @"
        <group>
            <subgroup>
                <text hint-style="base" hint-align="left">Deadline</text>
                 <text hint-style="caption" hint-align="left">$(Get-Date -Date $Deadline -Format "G")</text>
            </subgroup>
            <subgroup>
                <text hint-style="base" hint-align="right">Time Remaining </text>
                <text hint-style="caption" hint-align="right">$($TimeSpan.Days) days $($TimeSpan.Hours) hours $($TimeSpan.Minutes) minutes  .</text>
            </subgroup>
        </group>
"@
    $ToastTemplate.toast.visual.binding.InnerXml = $ToastTemplateDeadline.toast.visual.binding.InnerXml

}


# Change up the headers as required
If ($HeaderFormat -eq "TitleOnly")
{
    $ToastTemplate.toast.visual.binding.group[0].subgroup.InnerXml = "<text hint-style=""title"" hint-wrap=""true"" >$Title</text>"
}
If ($HeaderFormat -eq "ImageOnly")
{
    $ToastTemplate.toast.visual.binding.group[0].subgroup.InnerXml = "<image src=""$ImageFile""/>"
}
If ($HeaderFormat -eq "ImageAndTitle")
{
    $ToastTemplate.toast.visual.binding.group[0].subgroup.InnerXml = "<text hint-style=""title"" hint-wrap=""true"" >$Title</text><image src=""$ImageFile""/>"
}

# Load the notification into the required format
CMTraceLog -Message  "Launching Office 365 Toast Notification"-Type 1 -LogFile $LogFile
$ToastXml = New-Object -TypeName Windows.Data.Xml.Dom.XmlDocument
$ToastXml.LoadXml($ToastTemplate.OuterXml)


# Display
[Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($app).Show($ToastXml)
# [Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($app).Show($ToastConfirmXML)

CMTraceLog -Message  "-------------------------------------------------" -Type 1 -LogFile $LogFile
CMTraceLog -Message  "Completed Office 365 Toast Notification Script" -Type 2 -LogFile $LogFile
CMTraceLog -Message  "-------------------------------------------------" -Type 1 -LogFile $LogFile
